{
    // Use IntelliSense to learn about possible attributes.
    // Hover to view descriptions of existing attributes.
    // For more information, visit: https://go.microsoft.com/fwlink/?linkid=830387
    "version": "0.2.0",
    "configurations": [
        {
            "name": "Attach to Chrome",
            "port": 9222,
            "request": "attach",
            "type": "chrome",
            "webRoot": "${workspaceFolder}"
        },
        {
            "name": "Listen for Xdebug",
            "type": "php",
            "request": "launch",
            "port": 9003,
            "pathMappings": {
                "/xampp/htdocs/lotto": "c:/xampp/htdocs/lotto"
            }
        },
        {
            "name": "Launch currently open script",
            "type": "php",
            "request": "launch",
            "program": "${file}",
            "cwd": "${fileDirname}",
            "port": 0,
            "runtimeArgs": [
                "-dxdebug.start_with_request=yes"
            ],
            "env": {
                "XDEBUG_MODE": "debug,develop",
                "XDEBUG_CONFIG": "client_port=${port}"
            },
            "pathMappings": {
                "/xampp/htdocs/lotto": "c:/xampp/htdocs/lotto"
            }
        },
        {
            "name": "Launch Built-in web server",
            "type": "php",
            "request": "launch",
            "runtimeArgs": [
                "-dxdebug.mode=debug",
                "-dxdebug.start_with_request=yes",
                "-S",
                "localhost:0"
            ],
            "program": "",
            "cwd": "${workspaceRoot}",
            "port": 9003,
            "serverReadyAction": {
                "pattern": "Development Server \\(http://localhost:([0-9]+)\\) started",
                "uriFormat": "http://localhost:%s",
                "action": "openExternally"
            },
            "pathMappings": {
                "/xampp/htdocs/lotto": "c:/xampp/htdocs/lotto"
            }
        },
        {
            "name": "Launch built-in server and Debug",
            "type": "php",
            "request": "launch",
            "noDebug": false,
            "runtimeArgs": [
                "-S",
                "localhost:8000",
                "-t",
                "."
            ],
            "cwd": "${workspaceRoot}/.",
            "serverReadyAction": {
                "action": "openExternally"
            },
            "pathMappings": {
                "/xampp/htdocs/lotto": "c:/xampp/htdocs/lotto"
            }
        },
        {
            "name": "Launch built-in server and Profile",
            "type": "php",
            "request": "launch",
            "noDebug": true,
            "runtimeArgs": [
                "-S",
                "localhost:8000",
                "-t",
                "."
            ],
            "cwd": "${workspaceRoot}/.",
            "serverReadyAction": {
                "action": "openExternally"
            },
            "profile": true,
            "openProfile": true,
            "pathMappings": {
                "/xampp/htdocs/lotto": "c:/xampp/htdocs/lotto"
            }
        },
        {
            "name": "Listen for Xdebug",
            "type": "php",
            "request": "launch",
            "pathMappings": {
                "/xampp/htdocs/lotto": "c:/xampp/htdocs/lotto"
            }
        }
    ]
}
